import './style.css'
import '../../css/layout.css'
import '../../css/spacing.css'

import { useState, useEffect, useRef } from 'react'

function Button({ item }) {
  const [active, setActive] = useState(false)
  return (
    <button
      className={`container-chip ${active && 'container-chip-active'}`}
      onClick={() => setActive(!active)}
    >
      {item}{' '}
      <img
        src={active ? './active.svg' : './inactive.svg'}
        alt="adicionar"
        width={active ? 15 : 10}
      />
    </button>
  )
}

function Filters({ title, lista }) {
  return (
    <div className="filter-containers">
      <h1 className="filter-title">{title}</h1>
      {lista.map((item) => {
        return <Button item={item} />
      })}
    </div>
  )
}

function Filter({ diasDaSemana, categorias, transactions, setTransactions }) {
  const [noFilterTransactions, setNoFilterTransactions] = useState([
    ...transactions,
  ])
