import './style.css'
import '../../css/layout.css'
import '../../css/spacing.css'

import { useState, useEffect, useRef } from 'react'

function Button({ item }) {
  return (
    <button className="container-chip">
      {item} <img src="./adicionar.svg" alt="adicionar" width="10" />
    </button>
  )
}

function Filters({ title, lista }) {
  return (
    <div className="filter-containers">
      <h1 className="filter-title">{title}</h1>
      {lista.map((item) => {
        return <Button item={item} />
      })}
    </div>
  )
}

function Filter({ diasDaSemana, transactions }) {
  const [categorias, setCategorias] = useState([])
  async function loadCategories() {

    transactions.forEach((transaction) => {
      const categoriaExistente = categorias.filter(
        (categoria) => categoria === transaction.category,
      )
      if (!categoriaExistente) {
        setCategorias([...categorias], transaction.category)
        categorias += transaction.category
      }
    })

    return categorias
  }
  return (
    <div className="container-filters flex-grow3">
      <Filters title="Dia da Semana" lista={diasDaSemana} />
      <Filters title="Categoria" lista={loadCategories()} />
      <div className="flex-column justify-center pl-3">
        <h1 className="filter-title">Valor</h1>
        <label className="filter-label flex-column mb-2">
          Min
          <input id="min-value" type="text" className="filter-input" />
        </label>
        <label className="filter-label flex-column">
          Max
          <input id="max-value" type="text" className="filter-input" />
        </label>
        <button className="btn-clear-filters">Limpar Filtros</button>
        <button className="btn-apply-filters">Aplicar Filtros</button>
      </div>
    </div>
  )
}

export default Filter
