import './style.css'
import '../../css/layout.css'
import '../../css/spacing.css'

import { useState, useEffect, useRef } from 'react'

function Button({ title,item, filterStatus, setFilterStatus }) {
  const [active, setActive] = useState(false)


  function handleAddWeekDays(item) {
    setActive(!active)
    if(title)
    if (!filterStatus.week_days.includes(item)) {
      setFilterStatus({
        week_days: [],
        categories: [],
        max_value: 0,
        min_value: 0,
      })
    }
    console.log(filterStatus)
  }
  return (
    <button
      className={`container-chip ${active && 'container-chip-active'}`}
      onClick={() => handleAddWeekDays(item)}
    >
      {item}{' '}
      <img
        src={active ? './active.svg' : './inactive.svg'}
        alt="adicionar"
        width={active ? 15 : 10}
      />
    </button>
  )
}

function Filters({ title, lista, filterStatus, setFilterStatus }) {
  return (
    <div className="filter-containers">
      <h1 className="filter-title">{title}</h1>
      {lista.map((item) => {
        return (
          <Button
            item={item}
            filterStatus={filterStatus}
            setFilterStatus={setFilterStatus}
            title={title}
          />
        )
      })}
    </div>
  )
}

function Filter({ diasDaSemana, categorias, transactions, setTransactions }) {
  const [noFilterTransactions, setNoFilterTransactions] = useState([
    ...transactions,
  ])
  const [filterStatus, setFilterStatus] = useState({
    week_days: [],
    categories: [],
    max_value: 0,
    min_value: 0,
  })

  function addCategoryFilter() {}

  console.log(categorias)

  return (
    <div className="container-filters flex-grow3">
      <Filters
        title="Dia da Semana"
        lista={diasDaSemana}
        filterStatus={filterStatus}
        setFilterStatus={setFilterStatus}

      />
      <Filters title="Categoria" lista={categorias} />
      <div className="flex-column justify-center pl-3">
        <h1 className="filter-title">Valor</h1>
        <label className="filter-label flex-column mb-2">
          Min
          <input id="min-value" type="text" className="filter-input" />
        </label>
        <label className="filter-label flex-column">
          Max
          <input id="max-value" type="text" className="filter-input" />
        </label>
        <button className="btn-clear-filters">Limpar Filtros</button>
        <button className="btn-apply-filters">Aplicar Filtros</button>
      </div>
    </div>
  )
}

export default Filter
