import './style.css'
import '../../css/layout.css'
import '../../css/spacing.css'

import { useState, useEffect, useRef } from 'react'
import { format } from 'date-fns'

function Head() {
  return (
    <div className="table-head flex-row justify-around ">
      <h3 className="column-title" id="date">
        Data
      </h3>
      <h3 className="column-title" id="week-day">
        Dia da Semana
      </h3>
      <h3 className="column-title">Descrição</h3>
      <h3 className="column-title">Categoria</h3>
      <h3 className="column-title" id="value">
        Valor
      </h3>
    </div>
  )
}

function Line({ transaction }) {
  const [showConfirm, setShowConfirm] = useState(false)

    switch (transaction.week_day) {
      case 0:
        console.log("As laranjas custam $0.59 o quilo.");
        break;
      case 1:
        console.log("Maçãs custam $0.32 o quilo.");
        break;
      case 2:
        console.log("Bananas custam $0.48 o quilo.");
        break;
      case 3:
        console.log("Cerejas custam $3.00 o quilo.");
        break;
      case 4:
      case "Mamões":
        console.log("Mangas e mamões custam $2.79 o quilo.");
        break;
      default:
        console.log("Desculpe, estamos sem nenhuma " + expr + ".");
    }

  return (
    <div
      className="table-line flex-row justify-around gap-05"
      key={transaction.id}
    >
      <h3 className="line-items" id="date">
        {format(new Date(transaction.date), 'dd/MM/yyyy')}
      </h3>
      <h3 className="line-items" id="week-day">
        {transaction.week_day}
      </h3>
      <h3 className="line-items">{transaction.description}</h3>
      <h3 className="line-items">{transaction.category}</h3>
      <h3
        className={`line-items ${
          transaction.type === 'credit' ? 'credit-value' : 'debit-value'
        }`}
        id="value"
      >
        R$ {transaction.value},00
      </h3>
      <div className="flex-row justify-between position">
        <img
          className="edit-icon"
          src="./editar.svg"
          alt="edit"
          width="20"
        ></img>
        <img
          className="delete-icon ml-2"
          src="./excluir.svg"
          alt="delete"
          width="20"
          onClick={() => setShowConfirm(!showConfirm)}
        ></img>
      </div>
      {showConfirm && <Confirm />}
    </div>
  )
}

function Confirm() {
  return (
    <div className="flex-column container-confirm-delete">
      Apagar Item?
      <div className="flex-row mt-1 gap-05">
        <button className="btn-actions-confirm-delete sim">Sim</button>
        <button className="btn-actions-confirm-delete nao">Não</button>
      </div>
    </div>
  )
}

function Table({ transactions, setTransactions }) {
  return (
    <div className="table mt-5">
      <Head />
      <div className="table-body">
        {transactions.map((transaction) => (
          <Line transaction={transaction} />
        ))}
      </div>
    </div>
  )
}

export default Table
