import './style.css'
import '../../css/layout.css'
import '../../css/spacing.css'

import InputMask from 'react-input-mask'

import { useState, useEffect, useRef } from 'react'
import { getDay, format, parse } from 'date-fns'

function Modal({ setShowModal }) {
  const [value, setValue] = useState('')
  const [category, setCategory] = useState('')
  const [date, setDate] = useState('')
  const [description, setDescription] = useState('')
  const [credit, setCredit] = useState(false)
  const [debit, setDebit] = useState(true)

  async function handleSubmit() {
    if (!value || !category) {
      alert('Preencha os campos obrigatórios.')
    }
    try {
      const body = {
        date,
        week_day: getDay(date),
        description,
        value,
        category,
        type: credit ? 'credit' : 'debit',
      }

      const response = await fetch('http://localhost:3333/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(body),
      })

      const data = await response.json()
      console.log(data)
    } catch (error) {
      console.log(error)
    }
  }
  return (
    <div className="modal">
      <div className="modal-container">
        <h1 className="modal-title">Adicionar Registro</h1>
        <img
          className="close-icon"
          src="close.svg"
          alt="close"
          onClick={() => setShowModal(false)}
        ></img>
        <div className="flex-row mt-5 mb-3">
          <button
            id="credit-button"
            className={
              credit ? 'button credit-button' : 'button inactive-credit-button'
            }
            onClick={() => {
              setCredit(!credit)
              setDebit(!debit)
            }}
          >
            Entrada
          </button>
          <button
            id="debit-button"
            className={
              debit ? 'debit-button button' : 'inactive-debit-button button'
            }
            onClick={() => {
              setCredit(!credit)
              setDebit(!debit)
            }}
          >
            Saída
          </button>
        </div>
        <label>
          Valor
          <input
            name="value"
            className="modal-input mb-2 mt-1"
            onChange={(e) => setValue(Number(e.target.value))}
            value={value}
          />
        </label>
        <label>
          Categoria
          <input
            name="category"
            className="modal-input mb-2 mt-1"
            onChange={(e) => setCategory(e.target.value)}
            value={category}
          />
        </label>
        <label>
          Data
          <InputMask
            mask="99/99/9999"
            name="date"
            className="modal-input mb-2 mt-1"
            onChange={(e) => setDate(parse(e.target.value, 'yyyy-MM-dd', new Date()))}
            value={date}
          />
        </label>
        <label>
          Descrição
          <input
            className="modal-input mb-2 mt-1"
            onChange={(e) => setDescription(e.target.value)}
            value={description}
          />
        </label>
        <button className="btn-insert" onClick={() => handleSubmit()}>
          Confirmar
        </button>
      </div>
    </div>
  )
}

export default Modal
