import './App.css'
import '../src/css/layout.css'
import '../src/css/spacing.css'
import Header from './components/Header'
import Filter from './components/Filter'
import Table from './components/Table'
import Modal from './components/Modal'

import { useState, useEffect, useRef } from 'react'

function App() {
  const [showFiltro, setShowFiltro] = useState(false)
  const [showModal, setShowModal] = useState(false)
  const [transactions, setTransactions] = useState([])

  useEffect(() => {
    loadTransactions()
  }, [])

  async function loadTransactions() {
    try {
      const response = await fetch('http://localhost:3333/transactions', {
        method: 'GET',
      })
      const data = await response.json()
      setTransactions(data)
      console.log(data)
    } catch (error) {
      console.log(error.message)
    }
  }

  async function handleTransactionDelete(transactionID) {
    console.log(transactionID)
    // await fetch('http://localhost:3333/transactions/1', {
    //   method: 'DELETE',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(body),
    // })

    await loadTransactions()
  }

  return (
    <div className="App">
      {showModal && (
        <Modal
          setShowModal={setShowModal}
          loadTransactions={loadTransactions}
        />
      )}
      <Header />
      <div className="container">
        <button
          className="open-filters-button"
          onClick={() => setShowFiltro(!showFiltro)}
        >
          <img src="./filtro.svg" alt="Logo" width="25" />
          Filtrar
        </button>
        <div className="flex-row">
          <div className="flex-column">
            {showFiltro && <Filter />}

            {
              <Table
                transactions={transactions}
                setTransactions={setTransactions}
                loadTransactions
              />
            }
          </div>
          <div className="flex-column align-center">
            <div className="flex-column container-resume p-4">
              <h1 className="resume-title">Resumo</h1>
              <div className="flex-row align-center justify-between">
                <h2>Entradas</h2>
                <span className="in">R$ 000</span>
              </div>
              <div className="flex-row align-center justify-between">
                <h2>Saídas</h2>
                <span className="out">R$ 000</span>
              </div>
              <div className="flex-row align-center justify-between balance-title mt-3">
                <h2>Saldo</h2>
                <span className="balance">R$ 000</span>
              </div>
            </div>
            <button className="btn-add" onClick={() => setShowModal(true)}>
              Adicionar Registro
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default App
