import './style.css'
import '../../css/layout.css'

import { useState, useEffect, useRef } from 'react'

function Button({ item }) {
  return (
    <button className="container-chip">
      {item} <img src="./adicionar.svg" alt="adicionar" width="10" />
    </button>
  )
}

function Filters({ title, lista }) {
  return (
    <div className="filter-containers">
      <h1 className="filter-title">{title}</h1>
      {lista.map((item) => {
        return <Button item={item} />
      })}
    </div>
  )
}

function Filter() {
  const [diasDaSemana, setDiasDaSemana] = useState([
    'Domingo',
    'Segunda',
    'Terça',
    'Quarta',
    'Quinta',
    'Sexta',
    'Sábado',
  ])
  const [categorias, setCategorias] = useState(['Contas', 'Lazer'])
  return (
    <div className="container-filters flex-grow3">
      <Filters title="Dia da Semana" lista={diasDaSemana} />
      <Filters title="Categoria" lista={categorias} />
      <div>
        <h1 className="filter-title">Valor</h1>
        <label>
          Min
          <input id="min-value" type="text" />
        </label>
        <label>
          Max
          <input id="max-value" type="text" />
        </label>
        <button>Limpar </button>
      </div>
    </div>
  )
}

export default Filter
